<script>
import Vue from 'vue'
// import VueInstagram from 'vue-instagram'
// Vue.use(VueInstagram)
import { Tweet, Timeline } from 'vue-tweet-embed'
import CardPosted from '~/components/CardPosted.vue'
import formSignUp from '~/components/FormSignUp.vue'
import formLogin from '~/components/FormLogin.vue'
import PostFunc from '~/components/PostFunc.vue'
import MainTitle from '~/components/PostFunc.vue'
import firebase from '~/components/firebaseInit'
import FormPost from '~/components/FormPost.vue'

export default {
    components:{
        'card-posted': CardPosted,
        // 'vue-instagram': VueInstagram,
        'v-tweet': Tweet,
        'v-timeline': Timeline,
        'form-signup': formSignUp,
        'post-func': PostFunc,
        'main-title': MainTitle,
        'form-login': formLogin,
        'form-post': FormPost
    },
    created(){
        if (firebase.auth().currentUser) {
            this.currentUser = true;
            this.userID = firebase.auth().currentUser.email;
            // this.userName
            alert('userID:'+this.userID+'is logged in')
        }
        
    },
    data(){
        return{
            childTitle:　'コミュニティ',
            twitterID: 'stockohei',
            makeAccount:false,
            currentUser: false,
            openSignup: false,
            openLogin:false,
            openPost:false,
            // postの格納する内容
            userID:'',
            userName: '',
            postContent:'',

            communityCards:[
                {
                    'who': 'User Name',
                    'whoImg': 'data:image/png;base64,',
                    'where': 'Twitter',
                    'what': 'This post is described as textbook in the future',
                    'likes': 40,
                    // 'images':[
                    //     'data:image/jpeg;base64,',
                    //     'data:image/jpeg;base64,',
                    //     'data:image/jpeg;base64,',
                    //     'data:image/jpeg;base64,',
                    // ]
                },
            ]
        }
    },
    methods:{
        onPost(){
            if(firebase.auth().currentUser){
                firebase.collection("posts").doc(this.userID).set({
                    userID: userID,
                    userName: userName,
                    postContent: postContent
                })
                .then(function() {
                    alert("Document successfully written!" + userID + ':' +userName);
                    this.$router.push('/')
                })
                .catch(function(error) {
                    alert("Error writing document: ", error);
                });
            }else{
                this.openLogin = true;
                alert('you re not logged in')
            }
        }
    }
}
